        .append('svg')
        .attr('width', width + margin.left + margin.right)
        .attr('height', height + margin.top + margin.bottom);

    const g = svg.append('g')
        .attr('transform', `translate(${margin.left},${margin.top})`);

    // Add title
    svg.append('text')
        .attr('x', (width + margin.left + margin.right) / 2)
        .attr('y', 30)
        .attr('text-anchor', 'middle')
        .attr('class', 'chart-main-title')
        .style('font-size', '16px')
        .style('font-weight', 'bold')
        .style('fill', '#2c3e50');

    // Add axes labels
    g.append('text')
        .attr('x', width / 2)
        .attr('y', height + 50)
        .attr('text-anchor', 'middle')
        .attr('class', 'axis-label')
        .text('Box Office Rank (1 = Highest Grossing)');

    g.append('text')
        .attr('transform', 'rotate(-90)')
        .attr('x', -height / 2)
        .attr('y', -50)
        .attr('text-anchor', 'middle')
        .attr('class', 'axis-label')
        .text('Year');

    // Add rank labels (x-axis)
    g.selectAll('.rank-label')
        .data(ranks)
        .enter()
        .append('text')
        .attr('class', 'rank-label')
        .attr('x', d => xScale(d) + xScale.bandwidth() / 2)
        .attr('y', height + 25)
        .text(d => d);

    // Add year labels (y-axis)
    g.selectAll('.year-label')
        .data(years)
        .enter()
        .append('text')
        .attr('class', 'year-label')
        .attr('x', -15)
        .attr('y', d => yScale(d) + yScale.bandwidth() / 2 + 4)
        .text(d => d);

    // Add grid lines for better readability
    g.selectAll('.grid-line-x')
        .data(ranks.slice(1))
        .enter()
        .append('line')
        .attr('class', 'grid-line-x')
        .attr('x1', d => xScale(d))
        .attr('x2', d => xScale(d))
        .attr('y1', 0)
        .attr('y2', height)
        .style('stroke', '#e0e0e0')
        .style('stroke-width', 0.5);

    g.selectAll('.grid-line-y')
        .data(years.slice(1))
        .enter()
        .append('line')
        .attr('class', 'grid-line-y')
        .attr('x1', 0)
        .attr('x2', width)
        .attr('y1', d => yScale(d))
        .attr('y2', d => yScale(d))
        .style('stroke', '#e0e0e0')
        .style('stroke-width', 0.5);

    // Create circles for each movie
    g.selectAll('.circle')
        .data(data)
        .enter()
        .append('circle')
        .attr('class', 'circle')
        .attr('cx', d => xScale(d.rank) + xScale.bandwidth() / 2)
        .attr('cy', d => yScale(d.year) + yScale.bandwidth() / 2)
        .attr('r', d => radiusScale(d.worldwide_gross))
        .attr('fill', d => colorScale(d.year))
        .attr('opacity', 0.8)
        .on('mouseover', function(event, d) {
            // Highlight the circle
            d3.select(this)
                .attr('opacity', 1)
                .attr('stroke', '#333')
                .attr('stroke-width', 2);

            // Show tooltip
            tooltip.transition()
                .duration(200)
                .style('opacity', 1);

            tooltip.html(`
                <strong>${d.title}</strong><br/>
                Year: ${d.year}<br/>
                Rank: #${d.rank}<br/>
                Box Office: ${formatCurrency(d.worldwide_gross)}<br/>
                <em>Click to view on Wikipedia</em>
            `)
                .style('left', (event.pageX + 15) + 'px')
                .style('top', (event.pageY - 10) + 'px');
        })
        .on('mousemove', function(event) {
            tooltip
                .style('left', (event.pageX + 15) + 'px')
                .style('top', (event.pageY - 10) + 'px');
        })
        .on('mouseout', function() {
            // Remove highlight
            d3.select(this)
                .attr('opacity', 0.8)
                .attr('stroke', 'none');

            // Hide tooltip
            tooltip.transition()
                .duration(500)
                .style('opacity', 0);
        })
        .on('click', function(event, d) {
            window.open(d.link, '_blank');
        });

    // Add legend for circle sizes
    const legendData = [
        { value: 100, label: '₹100 cr' },
        { value: 500, label: '₹500 cr' },
        { value: 1000, label: '₹1000 cr' }
    ];

    const legend = g.append('g')
        .attr('class', 'size-legend')
        .attr('transform', `translate(${width - 150}, 20)`);

    legend.append('text')
        .attr('x', 0)
        .attr('y', -10)
        .style('font-size', '12px')
        .style('font-weight', 'bold')
        .style('fill', '#333')
        .text('Box Office Scale:');

    legendData.forEach((item, i) => {
        const legendItem = legend.append('g')
            .attr('transform', `translate(0, ${i * 30 + 10})`);

        legendItem.append('circle')
            .attr('cx', radiusScale(item.value))
            .attr('cy', 0)
            .attr('r', radiusScale(item.value))
            .attr('fill', colorScale(2010))
            .attr('opacity', 0.7);

        legendItem.append('text')
            .attr('x', radiusScale(item.value) * 2 + 10)
            .attr('y', 4)
            .style('font-size', '11px')
            .style('fill', '#333')
            .text(item.label);
    });

    // Add color legend for years
    const colorLegend = g.append('g')
        .attr('class', 'color-legend')
        .attr('transform', `translate(${width - 150}, 150)`);

    colorLegend.append('text')
        .attr('x', 0)
        .attr('y', -10)
        .style('font-size', '12px')
        .style('font-weight', 'bold')
        .style('fill', '#333')
        .text('Year Range:');

    const colorLegendHeight = 100;
    const colorGradient = colorLegend.append('defs')
        .append('linearGradient')
        .attr('id', 'color-gradient')
        .attr('gradientUnits', 'userSpaceOnUse')
        .attr('x1', 0)
        .attr('y1', colorLegendHeight)
        .attr('x2', 0)
        .attr('y2', 0);

    colorGradient.selectAll('stop')
        .data(d3.range(0, 1.1, 0.1))
        .enter()
        .append('stop')
        .attr('offset', d => d)
        .attr('stop-color', d => colorScale(1994 + d * 30));

    colorLegend.append('rect')
        .attr('x', 0)
        .attr('y', 0)
        .attr('width', 15)
        .attr('height', colorLegendHeight)
        .style('fill', 'url(#color-gradient)');

    colorLegend.append('text')
        .attr('x', 25)
        .attr('y', 4)
        .style('font-size', '11px')
        .style('fill', '#333')
        .text('2024');

    colorLegend.append('text')
        .attr('x', 25)
        .attr('y', colorLegendHeight)
        .style('font-size', '11px')
        .style('fill', '#333')
        .text('1994');

    // Add animation on load
    g.selectAll('.circle')
        .attr('r', 0)
        .transition()
        .duration(1000)
        .delay((d, i) => i * 20)
        .attr('r', d => radiusScale(d.worldwide_gross))
        .ease(d3.easeBounce);

    console.log(`Loaded ${data.length} movies across ${years.length} years`);
}

// Initialize the chart when the page loads
document.addEventListener('DOMContentLoaded', createChart);

