        .attr('y', 30)
        .attr('text-anchor', 'middle')
        .attr('class', 'chart-main-title')
        .style('font-size', '16px')
        .style('font-weight', 'bold')
        .style('fill', '#2c3e50');

    // Add axes labels
    g.append('text')
        .attr('x', width / 2)
        .attr('y', height + 50)
        .attr('text-anchor', 'middle')
        .attr('class', 'axis-label')
        .text('Box Office Rank (1 = Highest Grossing)');

